import React, {Component} from 'react'

class TodoApp extends Component {


    render() {
        return (
            <div className="TodoApp" >
                <LoginComponent></LoginComponent>
            </div>
        )
    }
}

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username : "jatin narula",
            password : ""
        }
        this.usernameChangeHandler = this.usernameChangeHandler.bind(this);
        this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
    }
    usernameChangeHandler(event) {
        console.log("username handler called")
        this.setState({username:event.target.value})
    } 
    passwordChangeHandler(event) {
        console.log("password handler called")
        this.setState({password:event.target.value})
    } 
    render() {
        return (
            <div>
                User Name : <input type="text" name="username" value={this.state.username} onChange={this.usernameChangeHandler}></input>
                Password : <input type="password" name="password" value={this.state.password}></input>
                <button>Login</button>
            </div>
            );
    }
}
export default TodoApp;
