import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'

class TodoApp extends Component {


    render() {
        return (
            <div className="TodoApp" >

                    <Router>
                        <Switch>
                            <Route path = "/" exact component = {LoginComponent} />
                            <Route path = "/login" component = {LoginComponent} />
                            <Route path = "/todo" component = {ListTodoComponent} />
                            <Route path = "/welcome/:name" component = {WelcomeComponent} />
                            <Route component = {ErrorComponent} />
                        </Switch>
                    </Router>  


             {  /* <LoginComponent></LoginComponent>
                <WelcomeComponent></WelcomeComponent>*/}
            </div>
        )
    }
}
class WelcomeComponent extends Component {
    render() {
        return (
            <div>
                Welcome to my App . {this.props.match.params.name}
            </div>
        )
    }
}
class ErrorComponent extends Component {
    render() {
        return (
            <div>
                An error occured!
            </div>
        )
    }
}
class ListTodoComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos : 
            [
                {id : 1, description : 'Learn React'},
                {id : 2, description : 'Learn SpringBoot'},
                {id : 3, description : 'Learn Design patterns'}
            ]
        }

    }
    render() {
        return (
        <div>
            <h1>List Todos</h1>
            <table>
                <thead>
                    <tr>
                        <th>id</th>
                        <th>description</th>    
                    </tr>
                </thead>
                <tbody>
                    {
                        this.state.todos.map( todo =>
                            <tr>
                                <td>{this.state.todo.id}</td>
                                <td>{this.state.todo.description}</td>
                            </tr>
                        )
                    }

                </tbody>
            </table>
        </div>
        )
    }
}
class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username : "jatin narula",
            password : "",
            hasLoginFailed: false,
            showSuccessMessage: false
        }
        // this.usernameChangeHandler = this.usernameChangeHandler.bind(this);
        // this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
        this.ChangeHandler = this.ChangeHandler.bind(this);
        this.loginAuth = this.loginAuth.bind(this);
    }
    ChangeHandler(event) {
        this.setState({[event.target.name]:event.target.value})
        //  console.log(this.state)
    } 
    // passwordChangeHandler(event) {
    //     console.log("password handler called")
    //     this.setState({password:event.target.value})
    // } 
    loginAuth() {
        console.log(this.state.username)
        if(this.state.username === 'jatin' && this.state.password === 'narula') {
            console.log("login successful")
            this.props.history.push(`/todo`)
            // this.setState({showSuccessMessage : true})
            // this.setState({hasLoginFailed : false})

        }
        else {
            console.log("login failed")
            this.setState({showSuccessMessage : false})
            this.setState({hasLoginFailed : true})
        }
    }

    render() {
        return (
            <div>
                <ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}/>
                <ShowLoginSuccessMessage showSuccessMessage={this.state.showSuccessMessage} />
                User Name : <input type="text" name="username" value={this.state.username} onChange={this.ChangeHandler}></input>
                Password : <input type="password" name="password" value={this.state.password} onChange={this.ChangeHandler}></input>
                <button onClick={this.loginAuth}>Login</button>
            </div>
            );
    }  
}

function ShowInvalidCredentials(props) {
    if(props.hasLoginFailed) {
        return <div>Invalid credentials</div>
    }
    return null
}

function ShowLoginSuccessMessage(props) {
    if(props.showSuccessMessage) {
        return <div>Login Successful</div>
    }
    return null
}



export default TodoApp;
