import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom'

class TodoApp extends Component {


    render() {
        return (
            <div className="TodoApp" >

                    <Router>
                        <HeaderComponent></HeaderComponent>
                        <Switch>
                            <Route path = "/" exact component = {LoginComponent} />
                            <Route path = "/login" component = {LoginComponent} />
                            <Route path = "/todo" component = {ListTodoComponent} />
                            <Route path = "/welcome/:name" component = {WelcomeComponent} />
                            <Route path = "/logout" component = {LogoutComponent} />
                            <Route component = {ErrorComponent} />
                        </Switch>
                        <FooterComponent></FooterComponent>

                    </Router>  


             {/* <LoginComponent></LoginComponent>
                <WelcomeComponent></WelcomeComponent>*/}
            </div>
        )
    }
}
class WelcomeComponent extends Component {
    render() {
        return (
            <div>
                Welcome to my App . {this.props.match.params.name}. See Todo <Link to="/todo">Here</Link>
            </div>
        )
    }
}
class ErrorComponent extends Component {
    render() {
        return (
            <div>
                An error occured!
            </div>
        )
    }
}
class HeaderComponent extends Component {
    render() {
        return (
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a className="navbar-brand">Todo Application</a></div>
                    <ul className="navbar-nav">
                        <li><Link className="nav-link" to="/todo">Home</Link></li>
                        <li ><Link className="nav-link" to="/todo">Todo</Link></li>    
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        <li ><Link className="nav-link" to="/login">Login</Link></li>
                        <li><Link className="nav-link" to="/logout">Logout</Link></li>
                    </ul>   
                </nav>
            </header>
        )
    }
}

class FooterComponent extends Component {
    render() {
        return (
            <footer className="footer">
                <span className="text-muted">All rights reserved 2018 @ jatin.jt.narula</span>
               <hr/> Footer 
            </footer>
        )
    }
}

class LogoutComponent extends Component {
    render() {
        return (
            <div>
               <h1> You are logged out.</h1>
               <div className="container">Thank you for using application.</div> 
            </div>
        )
    }
}
class ListTodoComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos : 
            [
                {id : 1, description : 'Learn React', done : false, targetDate : new Date()},
                {id : 2, description : 'Learn SpringBoot', done : false, targetDate : new Date()},
                {id : 3, description : 'Learn Design patterns', done : false, targetDate : new Date()}
            ]
        }

    }
    render() {
        return (
        <div>
            <h1>List Todos</h1>
            <table>
                <thead>
                    <tr>
                        <th>id</th>
                        <th>description</th>  
                        <th>Is Completed</th>
                        <th>Target Date</th>  
                    </tr>
                </thead>
                <tbody>
                    {
                        this.state.todos.map( todo =>
                            <tr>
                                <td>{todo.id}</td>
                                <td>{todo.description}</td>
                                <td>{todo.done.toString()}</td>
                                <td>{todo.targetDate.toString()}</td>
                            </tr>
                        )
                    }

                </tbody>
            </table>
        </div>
        )
    }
}
class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username : "jatin narula",
            password : "",
            hasLoginFailed: false,
            showSuccessMessage: false
        }
        // this.usernameChangeHandler = this.usernameChangeHandler.bind(this);
        // this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
        this.ChangeHandler = this.ChangeHandler.bind(this);
        this.loginAuth = this.loginAuth.bind(this);
    }
    ChangeHandler(event) {
        this.setState({[event.target.name]:event.target.value})
        //  console.log(this.state)
    } 
    // passwordChangeHandler(event) {
    //     console.log("password handler called")
    //     this.setState({password:event.target.value})
    // } 
    loginAuth() {
        console.log(this.state.username)
        if(this.state.username === 'jatin' && this.state.password === 'narula') {
            console.log("login successful")
            this.props.history.push(`/welcome/${this.state.username}`)
            // this.setState({showSuccessMessage : true})
            // this.setState({hasLoginFailed : false})

        }
        else {
            console.log("login failed")
            this.setState({showSuccessMessage : false})
            this.setState({hasLoginFailed : true})
        }
    }

    render() {
        return (
            <div>
                <h1> Login </h1>
                <div class="container">
                    <ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}/>
                    <ShowLoginSuccessMessage showSuccessMessage={this.state.showSuccessMessage} />
                    User Name : <input type="text" name="username" value={this.state.username} onChange={this.ChangeHandler}></input>
                    Password : <input type="password" name="password" value={this.state.password} onChange={this.ChangeHandler}></input>
                    <button onClick={this.loginAuth}>Login</button>
                </div>

            </div>
            );
    }  
}

function ShowInvalidCredentials(props) {
    if(props.hasLoginFailed) {
        return <div>Invalid credentials</div>
    }
    return null
}

function ShowLoginSuccessMessage(props) {
    if(props.showSuccessMessage) {
        return <div>Login Successful</div>
    }
    return null
}



export default TodoApp;
